// 可根据需要替换为真实的 base64 / 远程 URL
const CLICK_SRC = 'data:audio/mp3;base64,'
const SUCCESS_SRC = 'data:audio/mp3;base64,'
const FAIL_SRC = 'data:audio/mp3;base64,'

type AudioKind = 'click' | 'success' | 'fail'

interface PlayOptions {
    interrupt?: boolean
    onError?: (err: { code: number; msg: string }) => void
    onPlay?: () => void
}

export function useAudio() {
    let ctx: ReturnType<typeof uni.createInnerAudioContext> | null = null
    let currentKind: AudioKind | null = null
    let isPlaying = false

    const ensureCtx = () => {
        if (!ctx) {
            ctx = uni.createInnerAudioContext()
            ctx.obeyMuteSwitch = true
            ctx.autoplay = false
            ctx.onEnded(() => {
                isPlaying = false;
                currentKind = null
            })
            ctx.onStop(() => {
                isPlaying = false;
                currentKind = null
            })
            ctx.onError((e: any) => {
                isPlaying = false
                currentKind = null
                console.warn('[audio] 播放失败', e)
            })
        }
        return ctx
    }

    const srcMap: Record<AudioKind, string> = {
        click: CLICK_SRC,
        success: SUCCESS_SRC,
        fail: FAIL_SRC
    }

    const play = (kind: AudioKind, opts: PlayOptions = {}) => {
        const audio = ensureCtx()
        const src = srcMap[kind]
        if (!src) {
            opts.onError?.({code: -1, msg: '音频资源未配置'})
            return
        }
        if (isPlaying && !opts.interrupt) {
            return
        }
        if (isPlaying && opts.interrupt) {
            audio.stop()
        }
        currentKind = kind
        audio.src = src
        audio.seek(0)
        audio.play()
        isPlaying = true
        opts.onPlay?.()
    }

    const playClick = (o?: PlayOptions) => play('click', o)
    const playSuccess = (o?: PlayOptions) => play('success', o)
    const playFail = (o?: PlayOptions) => play('fail', o)

    return {
        playClick,
        playSuccess,
        playFail,
    }
}